mapper.select:
WITH RNUMRSVT AS ( SELECT ROWNUM RNUM, A.* FROM ( SELECT RSVT_NO , CST_NM , RSVT_YMD , RSVT_CHECKIN_YMD , RMT_TYPE_CD , RSVT_ROOM_CNT , RSVT_ADULT_CNT , RSVT_CHILD_CNT , RSVT_SE , CST_NO , RSVT_AMNT , RSVT_CNCL_YN , RSVT_SETLE_NO FROM TB_RSVT INNER JOIN TB_RSVT_DETAILS USING(RSVT_NO) INNER JOIN TB_CSTMR USING(CST_NO)
GROUP BY RSVT_NO , CST_NM , RSVT_YMD , RSVT_CHECKIN_YMD , RMT_TYPE_CD , RSVT_ROOM_CNT , RSVT_ADULT_CNT , RSVT_CHILD_CNT , RSVT_SE , CST_NO , RSVT_AMNT , RSVT_CNCL_YN , RSVT_SETLE_NO ORDER BY RSVT_NO DESC )A ) SELECT * FROM RNUMRSVT
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
