mapper.select:
WITH RNUMRSVT AS ( SELECT ROWNUM RNUM, A.* FROM ( SELECT CHKIN_NO , CST_NM , CI_DT , CI_COWILL_YMD , RM_NO , CO_DT ,CASE WHEN CI_SETLE_NO IS NOT NULL THEN CI_SETLE_NO ELSE '0' END AS CI_SETLE_NO , CASE WHEN CI_CAR_NO IS NOT NULL THEN CI_CAR_NO ELSE 'X' END AS CI_CAR_NO FROM TB_CHKOUT INNER JOIN TB_CHKIN USING (CHKIN_NO) INNER JOIN TB_CSTMR USING (CST_NO)
ORDER BY CHKIN_NO DESC )A ) SELECT * FROM RNUMRSVT
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
